import React, { useState } from 'react';
import { Divider, TextField, Button, IconButton } from '@mui/material';
import Input from '@mui/material/Input';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import { socket } from '../socket.js';
import './Multi.scss';
import Game from './Game';

export default function Multi(props) {
	const [state, setState] = useState({
		name: localStorage.getItem('name') || '',
		joinId: '',
		createId: '',
		joinError: '',
		game: false,
	});

	const onJoin = () => {
		socket.emit('join', { id: state.joinId }, (response) => {
			if (response.status) {
				setState({
					...state,
					game: true,
				});
			} else {
				setState({
					...state,
					joinError: response.reason,
				});
			}
		});
	};

	const onCreate = () => {
		socket.emit('create', {}, (code) => {
			setState({
				...state,
				createId: code,
			});
		});
	};

	return state.game ? (
		<Game />
	) : (
		<div id='multi' className='page'>
			<div id='sidebar'>
				{/* <TextField label='Name' variant='outlined' style={{ marginTop: '20px' }} /> */}
				<Input
					placeholder='Name'
					style={{ marginTop: '20px', padding: '12px' }}
					value={state.name}
					onChange={(event) => {
						let value = event.target.value.match(/[a-zA-Z0-9]+/g).join('');

						localStorage.setItem('name', value);

						setState({
							...state,
							name: value,
						});
					}}
				/>
				<Divider />
				<Button
					style={{ backgroundColor: '#266308' }}
					onClick={() => {
						if (state.joinId == '') return;
						onJoin();
					}}
				>
					Join
				</Button>
				<Input
					placeholder='ID...'
					style={{ marginTop: '10px' }}
					value={state.joinId}
					onChange={(event) => {
						setState({
							...state,
							joinId: event.target.value,
						});
					}}
				/>
				<div
					className='error'
					style={{
						display: state.joinError == '' ? 'none' : 'block',
					}}
				>
					{state.joinError}
				</div>

				<Divider />
				<Button style={{ backgroundColor: '#266308' }} onClick={onCreate}>
					Create
				</Button>
				<div
					id='created'
					style={{
						display: state.createId == '' ? 'none' : 'flex',
					}}
				>
					<div id='code'>
						<div>{state.createId}</div>

						<IconButton
							style={{ marginLeft: '2px' }}
							onClick={() => {
								navigator.clipboard.writeText(state.createId);
							}}
						>
							<ContentCopyIcon
								sx={{
									width: '16px',
									height: '16px',
									color: 'green',
								}}
							/>
						</IconButton>
					</div>
					<div id='players'>Waiting...</div>
				</div>
			</div>
		</div>
	);
}
