import React, { useState } from 'react';
import { Divider, TextField, Button, IconButton } from '@mui/material';
import Input from '@mui/material/Input';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import './Multi.scss';

export default function Multi(props) {
	const [state, setState] = useState({
		name: localStorage.getItem('name') || '',
		joinId: '',
	});

	const onJoin = () => {
		let id = state.joinId;
	};

	const onCreate = () => {};

	return (
		<div id='multi' className='page'>
			<div id='sidebar'>
				{/* <TextField label='Name' variant='outlined' style={{ marginTop: '20px' }} /> */}
				<Input
					placeholder='Name'
					style={{ marginTop: '20px', padding: '12px' }}
					value={state.name}
					onChange={(event) => {
						let value = event.target.value.match(/[a-zA-Z0-9]+/g).join('');

						localStorage.setItem('name', value);

						setState({
							...state,
							name: value,
						});
					}}
				/>
				<Divider />
				<Button
					style={{ backgroundColor: '#266308' }}
					onClick={() => {
						if (state.joinId == '') return;
						onJoin();
					}}
				>
					Join
				</Button>
				<Input
					placeholder='ID...'
					style={{ marginTop: '10px' }}
					value={state.joinId}
					onChange={(event) => {
						setState({
							...state,
							joinId: event.target.value,
						});
					}}
				/>
				<Divider />
				<Button style={{ backgroundColor: '#266308' }} onClick={onCreate}>
					Create
				</Button>
				<div id='created'>
					<div id='code'>
						<div>Aj1x901nzx0c9,</div>

						<IconButton style={{ marginLeft: '2px' }}>
							<ContentCopyIcon
								sx={{
									width: '16px',
									height: '16px',
									color: 'green',
								}}
							/>
						</IconButton>
					</div>
					<div id='players'>1/2 players</div>
				</div>
			</div>
		</div>
	);
}
