import React from 'react';
import { socket } from '../socket.js';
import { useLocation } from 'react-router-dom';
import './Game.scss';

const cellSize = 80;

// for (let i = 0; i < 64; i++) {
// 	let row = Math.floor((63 - i) / 8) + 1;
// 	let rank = ranks[i % 8];
// 	let position = `${rank}${row}`;

// 	// console.log([i, i % 2, Math.floor(i / 8)]);
// 	let square = document.createElement('div');

// 	square.setAttribute('data-position', position);

// 	square.className = `square ${row % 2 == 0 ? (i % 2 == 0 ? 'light' : 'dark') : i % 2 == 0 ? 'dark' : 'light'}`;
// 	square.style.backgroundColor = board.appendChild(square);

// 	square.addEventListener('click', () => {
// 		onPickPiece(square, square.getAttribute('data-position'));
// 	});
// }

const Square = (props) => {
	let row = Math.floor((63 - props.index) / 8) + 1;
	let rank = 'abcdefgh'[props.index % 8];
	let color = row % 2 == 0 ? (props.index % 2 == 0 ? 'light' : 'dark') : props.index % 2 == 0 ? 'dark' : 'light';
	if (props.color == 'b') {
		row = 8 - Math.floor((63 - props.index) / 8);
		rank = 'abcdefgh'.split('').reverse()[props.index % 8];
	}

	return (
		<div
			className={`square ${color}`}
			data-position={`${rank}${row}`}
			style={{
				width: cellSize,
				height: cellSize,
			}}
		>
			<div className='rank indicator' style={{ color: color == 'light' ? '#d18b47' : '#ffce9e' }}>
				{row == 1 ? rank : null}
			</div>
			<div className='row indicator' style={{ color: color == 'light' ? '#d18b47' : '#ffce9e' }}>
				{props.index % 8 == 7 ? row : null}
			</div>
		</div>
	);
};

export default function Game() {
	const { state } = useLocation();

	console.log(useLocation());

	console.log(socket);
	return (
		<div id='game' className='page'>
			<div id='main'>
				<div id='left' className='side'></div>
				<div id='center'>
					<div id='header'>Rayan vs Yosuf</div>
					<div id='outer-board'>
						<div id='board'>
							{Array(64)
								.fill(0)
								.map((_, i) => {
									return <Square index={i} color={state.color} />;
								})}
						</div>
						<div id='overlay'></div>
					</div>
				</div>
				<div id='right' className='side'></div>
			</div>
		</div>
	);
}
