import React, { useState } from 'react';
import { socket } from '../socket.js';
import { useLocation } from 'react-router-dom';
import './Game.scss';

const cellSize = 80;

// for (let i = 0; i < 64; i++) {
// 	let row = Math.floor((63 - i) / 8) + 1;
// 	let rank = ranks[i % 8];
// 	let position = `${rank}${row}`;

// 	// console.log([i, i % 2, Math.floor(i / 8)]);
// 	let square = document.createElement('div');

// 	square.setAttribute('data-position', position);

// 	square.className = `square ${row % 2 == 0 ? (i % 2 == 0 ? 'light' : 'dark') : i % 2 == 0 ? 'dark' : 'light'}`;
// 	square.style.backgroundColor = board.appendChild(square);

// 	square.addEventListener('click', () => {
// 		onPickPiece(square, square.getAttribute('data-position'));
// 	});
// }
const fenTo2D = (fen) => {
	fen = fen.split(' ')[0];
	let total = 0;
	[...fen.matchAll(/\d/g)].map((match, index) => {
		let replacement = 'e'.repeat(parseInt(match[0]));
		let increase = replacement.length - 1;

		fen = fen.split('');
		fen.splice(match.index + total, 1, replacement);
		fen = fen.join('');

		total += increase;
	});

	let array = fen.split('/').map((row, index) => row.split(''));
	return array;
};

const Square = (props) => {
	return (
		<div
			className={`square ${props.shade}`}
			data-position={props.position}
			onClick={() => {
				props.onClick(props.position);
			}}
		>
			<div className='rank indicator' style={{ color: props.shade == 'light' ? '#d18b47' : '#ffce9e' }}>
				{row == 1 ? rank : null}
			</div>
			<div className='row indicator' style={{ color: props.shade == 'light' ? '#d18b47' : '#ffce9e' }}>
				{props.index % 8 == 7 ? row : null}
			</div>
		</div>
	);
};

export default function Game() {
	const location = useLocation();

	const getSquareDataByIndex = (index, color) => {
		let row = Math.floor((63 - index) / 8) + 1;
		let rank = 'abcdefgh'[index % 8];
		let shade = row % 2 == 0 ? (index % 2 == 0 ? 'light' : 'dark') : index % 2 == 0 ? 'dark' : 'light';
		if (color == 'b') {
			row = 8 - Math.floor((63 - index) / 8);
			rank = 'abcdefgh'.split('').reverse()[index % 8];
		}

		return {
			position: `${row}${rank}`,
			shade: shade,
			row: row,
			rank: rank,
		};
	};

	// console.log(useLocation());

	const onClick = (position) => {
		console.log(position);
	};

	console.log(socket);
	return (
		<div id='game' className='page'>
			<div id='main'>
				<div id='left' className='side'></div>
				<div id='center'>
					<div id='header'>Rayan vs Yosuf</div>
					<div id='outer-board'>
						<div id='board'>
							{Array(64)
								.fill(0)
								.map((_, i) => {
									let { position, shade } = getSquareDataByIndex(i, location.state.color);

									return <Square position={position} shade={shade} onClick={onClick} />;
								})}
						</div>
						<div id='overlay'></div>
					</div>
				</div>
				<div id='right' className='side'></div>
			</div>
		</div>
	);
}
