import React, { useState } from 'react';
import { socket } from '../socket.js';
import { useLocation } from 'react-router-dom';
import './Game.scss';

const cellSize = 80;

// for (let i = 0; i < 64; i++) {
// 	let row = Math.floor((63 - i) / 8) + 1;
// 	let rank = ranks[i % 8];
// 	let position = `${rank}${row}`;

// 	// console.log([i, i % 2, Math.floor(i / 8)]);
// 	let square = document.createElement('div');

// 	square.setAttribute('data-position', position);

// 	square.className = `square ${row % 2 == 0 ? (i % 2 == 0 ? 'light' : 'dark') : i % 2 == 0 ? 'dark' : 'light'}`;
// 	square.style.backgroundColor = board.appendChild(square);

// 	square.addEventListener('click', () => {
// 		onPickPiece(square, square.getAttribute('data-position'));
// 	});
// }
const fenTo2D = (fen) => {
	fen = fen.split(' ')[0];
	let total = 0;
	[...fen.matchAll(/\d/g)].map((match, index) => {
		let replacement = 'e'.repeat(parseInt(match[0]));
		let increase = replacement.length - 1;

		fen = fen.split('');
		fen.splice(match.index + total, 1, replacement);
		fen = fen.join('');

		total += increase;
	});

	let array = fen.split('/').map((row, index) => row.split(''));
	return array;
};

const Square = (props) => {
	return (
		<div
			className={`square ${props.shade} ${props.selected ? 'selected' : ''}`}
			data-position={props.position}
			onClick={() => {
				props.onClick(props.position);
			}}
		>
			<div className='rank indicator' style={{ color: props.shade == 'light' ? '#d18b47' : '#ffce9e' }}>
				{props.row == 1 ? props.rank : null}
			</div>
			<div className='row indicator' style={{ color: props.shade == 'light' ? '#d18b47' : '#ffce9e' }}>
				{props.index % 8 == 7 ? props.row : null}
			</div>
		</div>
	);
};

export default function Game() {
	const [state, setState] = useState({
		selected: null,
		pieces: [
			[
				{ square: 'a8', type: 'r', color: 'b' },
				{ square: 'b8', type: 'n', color: 'b' },
				{ square: 'c8', type: 'b', color: 'b' },
				{ square: 'd8', type: 'q', color: 'b' },
				{ square: 'e8', type: 'k', color: 'b' },
				{ square: 'f8', type: 'b', color: 'b' },
				{ square: 'g8', type: 'n', color: 'b' },
				{ square: 'h8', type: 'r', color: 'b' },
			],
			[
				{ square: 'a7', type: 'p', color: 'b' },
				{ square: 'b7', type: 'p', color: 'b' },
				{ square: 'c7', type: 'p', color: 'b' },
				{ square: 'd7', type: 'p', color: 'b' },
				{ square: 'e7', type: 'p', color: 'b' },
				{ square: 'f7', type: 'p', color: 'b' },
				{ square: 'g7', type: 'p', color: 'b' },
				{ square: 'h7', type: 'p', color: 'b' },
			],
			[null, null, null, null, null, null, null, null],
			[null, null, null, null, null, null, null, null],
			[null, null, null, null, null, null, null, null],
			[null, null, null, null, null, null, null, null],
			[
				{ square: 'a2', type: 'p', color: 'w' },
				{ square: 'b2', type: 'p', color: 'w' },
				{ square: 'c2', type: 'p', color: 'w' },
				{ square: 'd2', type: 'p', color: 'w' },
				{ square: 'e2', type: 'p', color: 'w' },
				{ square: 'f2', type: 'p', color: 'w' },
				{ square: 'g2', type: 'p', color: 'w' },
				{ square: 'h2', type: 'p', color: 'w' },
			],
			[
				{ square: 'a1', type: 'r', color: 'w' },
				{ square: 'b1', type: 'n', color: 'w' },
				{ square: 'c1', type: 'b', color: 'w' },
				{ square: 'd1', type: 'q', color: 'w' },
				{ square: 'e1', type: 'k', color: 'w' },
				{ square: 'f1', type: 'b', color: 'w' },
				{ square: 'g1', type: 'n', color: 'w' },
				{ square: 'h1', type: 'r', color: 'w' },
			],
		],
	});

	const location = useLocation();

	const onClick = (position) => {
		console.log(position);
		// setState({
		// 	selected: position,
		// });
		// console.log(position);
	};

	const getSquareDataByIndex = (index, color) => {
		let row = Math.floor((63 - index) / 8) + 1;
		let rank = 'abcdefgh'[index % 8];
		let shade = row % 2 == 0 ? (index % 2 == 0 ? 'light' : 'dark') : index % 2 == 0 ? 'dark' : 'light';
		if (color == 'b') {
			row = 8 - Math.floor((63 - index) / 8);
			rank = 'abcdefgh'.split('').reverse()[index % 8];
		}

		return {
			position: `${rank}${row}`,
			shade: shade,
			row: row,
			rank: rank,
		};
	};

	// console.log(useLocation());

	return (
		<div id='game' className='page'>
			<div id='main'>
				<div id='left' className='side'></div>
				<div id='center'>
					<div id='header'>Rayan vs Yosuf</div>
					<div id='outer-board'>
						<div id='board'>
							{state.pieces.map((row, rowIndex) => {
								row.map((piece, rankIndex) => {
									console.log(piece);
									console.log(`${'abcdefgh'[rankIndex]}${rowIndex}`);
								});
							})}
							{/* {Array(64)
								.fill(0)
								.map((_, i) => {
									let { position, shade, row, rank } = getSquareDataByIndex(i, location.state.color);

									return <Square position={position} shade={shade} row={row} rank={rank} onClick={onClick} selected={position == state.selected} />;
								})} */}
						</div>
						<div id='overlay'></div>
					</div>
				</div>
				<div id='right' className='side'></div>
			</div>
		</div>
	);
}
