	};

	const getPieces = () => {
		let pieces = state.pieces;

		if (state.color == 'b') {
			pieces.reverse();
			pieces = pieces.map((row) => row.reverse());
		}

		return pieces;
	};

	return (
		<div id='game' className='page'>
			<div id='main'>
				<div id='left' className='side'></div>
				<div id='center'>
					<div id='header'>Rayan vs Yosuf</div>
					<div id='outer-board'>
						<div
							id='board'
							style={{
								pointerEvents: state.overlay ? 'none' : 'all',
							}}
						>
