										let row = 8 - rowIndex;
										let rank = rankIndex;
										let shade = row % 2 == 0 ? (rank % 2 == 0 ? 'light' : 'dark') : rank % 2 == 0 ? 'dark' : 'light';

										if (state.color == 'b') {
